import WeatherIcon from "./WeatherIcon";
import { DateTime } from "luxon";

export default function ForecastDay(props) {
    const tomorrow = DateTime.now().plus({ days: 1 }).weekdayLong;
    const lastDay= DateTime.now().plus({ days: 5 }).weekdayLong;

    let day;
    if (props.data.date === tomorrow || props.data.date === lastDay) {
      day = props.data.date;
    } else {
      day = props.data.date.slice(0, 3);
    }


  function maxTemperature() {
    let temperature = Math.round(props.data.temperature.maximum);
    return `${temperature}째`;
  }

  function minTemperature() {
    let temperature = Math.round(props.data.temperature.minimum);
    return `${temperature}째`;
  }

  return (
    <div>
      <div className="day">{day}</div>
      <WeatherIcon code={props.data.condition.icon} size={38} />
      <div className="forecast-temperature">
        <span className="forecast-max">{maxTemperature()}</span>
        <span className="forecast-min">{minTemperature()}</span>
      </div>
    </div>
  );
}
